//按几率获取低级技能编号
int get_drop_skill1()
{
	int i,size,p=0,ski=74101;
	int rate = random(10000);
	string *nTmp;
	
	nTmp = keys(mpDropSkill1);
	for(i=0,size=sizeof(nTmp);i<size;i++)
	{
		p += mpDropSkill1[nTmp[i]];
		if(rate>=p)	continue;
		ski = mpBeastSkill[nTmp[i]];
		break;
	}
	return ski;
}

//随机获取高级技能编号
int get_drop_skill2()
{
	int ski;
	string *nTmp;
	nTmp = keys(mpDropSkill2);
	ski = mpBeastSkill[nTmp[random(sizeof(nTmp))]];
	if ( !ski )
		ski=74201;
	return ski;
}

