{
	return mpSkiDesc[skill];	
}
//初始化精元丹技能 flag: 1 低级 2 高级
int init_item_skill(object item,int flag)
{
	int skill;
	
	if ( !item )
		return 0;
	if ( flag == 2 )
		skill = get_drop_skill2();
	else
		skill = get_drop_skill1();
	item->set("special",sprintf("%d",skill));
	return 1;
}

//捕抓玉兔类的特殊处理
void get_yutu(object player, object rabbit)
{
	int rand,move;
	object item,present;
	string i;
	rand = random(100);  
	i = rabbit->get_name();
        if(rand < mpBeastYutu[i]["rand"])
        {
		if(sizeof_inventory(player, 1, MAX_CARRY) >= MAX_CARRY )
		{
			send_user(player,"%c%s",'!',"Trên người đạo cụ không gian không đủ!");
			return;
		}
        	//捕捉成功.
        	send_user(player, "%c%s", '!', "Bạn đã bắt " + rabbit->get_name());
        	rabbit->remove_from_scene();//从场景里面移出
        	rabbit->before_destruct();
		destruct(rabbit);
		if( objectp( item = present(mpBeastYutu[i]["name"], player, 1, MAX_CARRY) ) && item->get_amount() < 300 )
		{
			item->add_amount(1);				
		}
		else
		{
			present = new(mpBeastYutu[i]["file"]);
			if(!present)
			{
				return;
			}
			if(move = present->move(player,-1))
			{
				present->add_to_user(move);
			}	
			else
			{
				destruct(present);
				return;
			}
			return ;
		}
        }	
        else
        {
        	send_user(player, "%c%s", '!', "Bắt giữ "+rabbit->get_name()+" Thất Bại!");
        	return;
        }
}
